mapper.delete:
delete from gen_table_column
where table_id IN

    #{tableId}

mapper.update:
update gen_table_column
table_id=#{tableId,jdbcType=NUMERIC}, column_name=#{columnName,jdbcType=VARCHAR}, column_comment=#{columnComment,jdbcType=VARCHAR}, column_type=#{columnType,jdbcType=VARCHAR}, java_type=#{javaType,jdbcType=VARCHAR}, java_field=#{javaField,jdbcType=VARCHAR}, is_pk=#{isPk,jdbcType=VARCHAR}, is_increment=#{isIncrement,jdbcType=VARCHAR}, is_required=#{isRequired,jdbcType=VARCHAR}, is_insert=#{isInsert,jdbcType=VARCHAR}, is_edit=#{isEdit,jdbcType=VARCHAR}, is_list=#{isList,jdbcType=VARCHAR}, is_query=#{isQuery,jdbcType=VARCHAR}, query_type=#{queryType,jdbcType=VARCHAR}, html_type=#{htmlType,jdbcType=VARCHAR}, dict_type=#{dictType,jdbcType=VARCHAR}, sort=#{sort,jdbcType=NUMERIC}, search_value=#{searchValue,jdbcType=VARCHAR}, create_by=#{createBy,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, update_by=#{updateBy,jdbcType=VARCHAR}, update_time=#{updateTime,jdbcType=TIMESTAMP}, remark=#{remark,jdbcType=VARCHAR}, params=#{params},
where columnId = #{columnId,jdbcType=NUMERIC}

mapper.select:
select column_name,
       if(is_nullable='no' and column_key!='PRI','1',null) is_required,
       if(column_key='PRI','1','0') is_pk,
       ordinal_position as sort,
       column_comment,
       if(extra='auto_increment' ,'1','0') is_increment, column_type
from information_schema.columns
where table_schema = (select database())
    and table_name = #{tableName}
order by ordinal_position
