mapper.delete:
delete from gen_table
WHERE table_id in

    #{tableId}

mapper.update:
update gen_table
table_name=#{tableName}, table_comment=#{tableComment}, sub_table_name=#{subTableName}, sub_table_fk_name=#{subTableFkName}, class_name=#{className}, tpl_category=#{tplCategory}, package_name=#{packageName}, module_name=#{moduleName}, business_name=#{businessName}, function_name=#{functionName}, function_author=#{functionAuthor}, gen_type=#{genType}, gen_path=#{genPath}, options=#{options}, create_by=#{createBy}, create_time=#{createTime}, update_by=#{updateBy}, update_time=#{updateTime}, remark=#{remark},
where tableId = #{tableId}
